mapper.select:
SELECT a.exercise_feedback_request_no as exerciseFeedbackRequestNo, a.exercise_feedback_request_title as exerciseFeedbackRequestTitle, a.exercise_feedback_request_date as exerciseFeedbackRequestDate,
a.member_no as memberNo, a.teacher_no as teacherNo, b.member_id as memberId, b.member_name as memberName, c.exercise_feedback_result as exerciseFeedbackResult, c.exercise_feedback_approval as exerciseFeedbackApproval, c.exercise_feedback_approval_date as exerciseFeedbackApprovalDate FROM exercise_feedback_request a INNER JOIN member b ON a.member_no = b.member_no INNER JOIN exercise_feedback_approval_result c ON a.exercise_feedback_request_no = c.exercise_feedback_request_no WHERE a.member_no = #{memberNo}
b.member_id as memberId, b.member_name as memberName, c.exercise_feedback_result as exerciseFeedbackResult, c.exercise_feedback_approval as exerciseFeedbackApproval, c.exercise_feedback_approval_date as exerciseFeedbackApprovalDate FROM exercise_feedback_request a INNER JOIN member b ON a.member_no = b.member_no INNER JOIN exercise_feedback_approval_result c ON a.exercise_feedback_request_no = c.exercise_feedback_request_no WHERE a.teacher_no = #{memberNo}
a.member_no as memberNo, a.teacher_no as teacherNo, b.member_id as memberId, b.member_name as memberName, c.exercise_feedback_result as exerciseFeedbackResult, c.exercise_feedback_approval as exerciseFeedbackApproval, c.exercise_feedback_approval_date as exerciseFeedbackApprovalDate FROM exercise_feedback_request a INNER JOIN member b ON a.member_no = b.member_no INNER JOIN exercise_feedback_approval_result c ON a.exercise_feedback_request_no = c.exercise_feedback_request_no WHERE a.member_no = #{memberNo}
a.member_no as memberNo, a.teacher_no as teacherNo, c.exercise_feedback_result as exerciseFeedbackResult, c.exercise_feedback_approval as exerciseFeedbackApproval, c.exercise_feedback_approval_date as exerciseFeedbackApprovalDate FROM exercise_feedback_request a INNER JOIN exercise_feedback_approval_result c ON a.exercise_feedback_request_no = c.exercise_feedback_request_no
ORDER BY exercise_feedback_approval_date DESC LIMIT #{beginRow},#{pagePerRow}

SELECT
count(*)
FROM exercise_feedback_request
WHERE exercise_feedback_request_no > #{exerciseFeedbackRequestNo}

	
		
			AND member_no = #{memberNo}
		
		
			AND teacher_no = #{memberNo}
		
		
			AND member_no = #{memberNo}
		
	

 ORDER BY exercise_feedback_request_no ASC LIMIT 1

