mapper.select:
SELECT a.group_invite_no as groupInviteNo, a.group_name as groupName, a.personal_information_approval as personalInformationApproval, a.group_invite_approval_date as groupInviteApprovalDate, b.member_name as memberName, b.member_id as memberId, c.group_no as groupNo FROM group_invite_approval_result a INNER JOIN member b ON a.member_no = b.member_no INNER JOIN group_invite c ON a.group_invite_no = c.group_invite_no WHERE c.group_name = #{groupName}
AND a.group_invite_no like CONCAT('%', #{keyWord}, '%')
AND b.member_id like CONCAT('%', #{keyWord}, '%')
AND b.member_name like CONCAT('%', #{keyWord}, '%')
AND a.personal_information_approval like CONCAT('%', #{keyWord}, '%')
ORDER BY a.group_invite_no DESC LIMIT #{beginRow},#{pagePerRow}

mapper.insert:
INSERT INTO group_invite_approval_result(
group_invite_no, member_no, group_name, personal_information_approval, group_invite_approval_date
)
VALUES (
#{groupInviteNo}, #{memberNo}, #{groupName},#{personalInformationApproval}, NOW()
)

INSERT INTO group_invite(
group_invite_no, group_no, member_no, group_invite_approval, group_invite_message, group_invite_date
)
VALUES (
#{groupInviteNo}, #{groupNo}, #{memberNo}, #{groupInviteApproval}, #{groupInviteMessage}, NOW()
)

mapper.select:
SELECT a.group_invite_no as groupInviteNo, a.group_no as groupNo, a.member_no as memberNo, a.group_invite_date as groupInviteDate, a.group_invite_message as groupInviteMessage, b.group_name as groupName, b.group_Info as groupInfo, c.group_kind_name as groupKindName FROM group_invite a INNER JOIN group_create b ON a.group_no = b.group_no INNER JOIN group_kind c ON b.group_kind_no = c.group_kind_no
WHERE a.member_no = #{memberNo}
ORDER BY group_invite_date DESC LIMIT #{beginRow},#{pagePerRow}
